import React from 'react';

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch('/api/hello')
      .then(response => response.json())
      .then(data => setData(data))
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-4xl font-bold text-red-500">
          No-Code Project Creator 1
        </h1>
        {data && <p>{data.message}</p>}
      </div>
    </div>
  );
}

export default App;

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';
import './index.css';

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import { createServer } from 'vite';
import { startServer } from '../server/index.js';

async function startDev() {
  // Start the Express API server first
  await startServer();

  // Then start Vite in dev mode
  const vite = await createServer({
    configFile: './vite.config.js',
  });

  const x = await vite.listen();
  console.log(`Vite dev server running on port ${vite.config.server.port}`);
}

startDev();

import express from 'express';

const app = express();
const PORT = 3001;

// Body parsing middleware
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

app.get('/api/hello', (req, res) => {
  res.json({ message: 'Hello World!' });
});

// Export a function to start the server
export async function startServer() {
  try {
    app.listen(PORT, () => {
      console.log(`API Server running on port ${PORT}`);
    });
  } catch (err) {
    console.error('Failed to start server:', err);
    process.exit(1);
  }
}

// Start the server directly if this is the main module
if (import.meta.url === `file://${process.argv[1]}`) {
  startServer();
}

export default {
  content: [
    "./client/index.html",
    "./client/src/**/*.{js,jsx}"
  ],
  theme: {
    extend: {},
  },
  plugins: [],
}

import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";

export const vitePort = 3000;

export default defineConfig({
  plugins: [react()],
  root: path.join(process.cwd(), "client"),
  build: {
    outDir: path.join(process.cwd(), "dist"),
    emptyOutDir: true,
  },
  server: {
    host: true,
    port: vitePort,
    allowedHosts: true,
    proxy: {
      "/api": {
        target: "http://localhost:3001",
        changeOrigin: true,
      },
    },
  },
});
